create extension if not exists pgcrypto; create table if not exists providers(id uuid primary key default gen_random_uuid(),name text not null); create table if not exists posts(id uuid primary key default gen_random_uuid(),slug text unique not null,title text not null,html text not null,created_at timestamptz default now()); create table if not exists cities(id uuid primary key default gen_random_uuid(),slug text unique not null,city text not null,state text not null);